import React, { useState, useEffect } from "react";
import axios from "axios";
import "./main.css";

export default function Trending() {
	const [trendingTracks, setTrendingTracks] = useState([]);
	const token = window.localStorage.getItem("token");

	useEffect(() => {
		const fetchTrendingTracks = async () => {
			try {
				const response = await axios.get(
					"https://api.spotify.com/v1/browse/new-releases",
					{
						headers: {
							Authorization: `Bearer ${token}`,
						},
						params: {
							limit: 10, // Limiting to 10 trending tracks for demonstration
						},
					}
				);
				setTrendingTracks(response.data.albums.items);
				console.log(trendingTracks);
			} catch (error) {
				console.error("Error fetching trending tracks:", error);
			}
		};

		// Fetch trending tracks when the component mounts
		fetchTrendingTracks();

		// Clean up function
		return () => {
			// Any cleanup code if needed
		};
	}, [token]);

	return (
		<div>
			<div className="title">
				<h3>Trending</h3>
				<p>More</p>
			</div>
			<ul className="category-grid">
				{trendingTracks.map((track) => (
					<li key={track.id}>
						<img src={track.images[0].url} />
						<p>
							{" "}
							{track.name.length > 20
								? track.name.substring(0, 20) + "..."
								: track.name}
							-{" "}
							{track.artists
								.map((artist) => (
									<span
										key={artist.id}
										title={artist.name}>
										{artist.name.length > 20
											? artist.name.substring(0, 20) + "..."
											: artist.name}
									</span>
								))
								.join(", ")}
						</p>
					</li>
				))}
			</ul>
		</div>
	);
}
