import React, { useEffect, useState } from "react";
import "./home.css";
import Sidebar from "../../components/sidebar";
import RightSidebar from "../../components/right-sidebar";
import Main from "../../components/main/main";
import Login from "../../screens/auth/login";
import { setClientToken } from "../../spotify";

export default function Home() {
	const [token, setToken] = useState("");

	useEffect(() => {
		const hash = window.location.hash;
		window.location.hash = "";

		const isTokenExpired = (token) => {
			if (!token) return true; // Token is considered expired if it's not present

			const tokenParts = token.split(".");
			if (tokenParts.length !== 3) return true; // Token is invalid if it doesn't have three parts

			const tokenPayload = JSON.parse(atob(tokenParts[1])); // Decode token payload
			if (!tokenPayload.exp) return true; // Token is considered expired if it doesn't have an expiration time

			const expirationTime = tokenPayload.exp * 1000; // Convert expiration time to milliseconds
			const currentTime = new Date().getTime(); // Current time in milliseconds

			return expirationTime < currentTime; // Token is expired if expiration time is less than current time
		};

		const tokenFromStorage = window.localStorage.getItem("token");
		if (!tokenFromStorage && hash) {
			const _token = hash.split("&")[0].split("=")[1];
			window.localStorage.setItem("token", _token);
			setToken(_token);
			setClientToken(_token);
		} else if (tokenFromStorage && !isTokenExpired(tokenFromStorage)) {
			setToken(tokenFromStorage);
			setClientToken(tokenFromStorage);
		}
	}, []);

	return !token ? (
		<Login />
	) : (
		<div className="main-body">
			<Sidebar />
			<Main />
			<RightSidebar />
		</div>
	);
}
