// Playbar.js

import React from "react";
import "./main.css";

export default function Playbar({ audioRef }) {
	const playPause = () => {
		if (audioRef.current.paused) {
			audioRef.current.play();
		} else {
			audioRef.current.pause();
		}
	};

	const nextTrack = () => {
		const nextSibling = audioRef.current.parentNode.nextSibling;
		if (nextSibling) {
			const nextAudio = nextSibling.querySelector("audio");
			if (nextAudio) {
				audioRef.current.pause();
				audioRef.current.currentTime = 0;
				nextAudio.play();
			}
		}
	};

	const prevTrack = () => {
		const prevSibling = audioRef.current.parentNode.previousSibling;
		if (prevSibling) {
			const prevAudio = prevSibling.querySelector("audio");
			if (prevAudio) {
				audioRef.current.pause();
				audioRef.current.currentTime = 0;
				prevAudio.play();
			}
		}
	};

	const toggleMute = () => {
		audioRef.current.muted = !audioRef.current.muted;
	};

	const changeVolume = (value) => {
		audioRef.current.volume = value;
	};

	return (
		<div className="playbar">
			<audio
				ref={audioRef}
				controls></audio>

			<div className="icons-container">
				{/* ... (your icon components) */}

				<div className="iconzz">
					<div className="icon-container">
						<i
							className="fa-solid fa-backward"
							onClick={prevTrack}></i>
					</div>
					<div className="icon-containeer">
						<i
							className="fa-solid fa-circle-pause"
							onClick={playPause}></i>
					</div>
					<div className="icon-container">
						<i
							className="fa-solid fa-forward"
							onClick={nextTrack}></i>
					</div>
					<div className="icon-container">
						<i className="fa-solid fa-repeat"></i>
					</div>
				</div>

				<div className="iconzzz">
					<div className="icon-container">
						<i
							className="fa-solid fa-volume-xmark"
							onClick={toggleMute}></i>
					</div>
					<div className="icon-container">
						<i className="fa-solid fa-volume-high"></i>
						<input
							type="range"
							min="0"
							max="1"
							step="0.1"
							defaultValue="1"
							onChange={(e) => changeVolume(e.target.value)}
						/>
					</div>
				</div>
			</div>
		</div>
	);
}
