// Playbar.js

import React, { useState } from "react";

export default function Playbar({ currentTrack, audioRef }) {
	const [isPlaying, setIsPlaying] = useState(false);
	const [volume, setVolume] = useState(1);

	const playPause = () => {
		if (!audioRef.current.src && currentTrack) {
			audioRef.current.src = currentTrack.url; // Set the audio source to the current track URL
		}
		if (audioRef.current.paused) {
			audioRef.current.play();
		} else {
			audioRef.current.pause();
		}
	};

	const nextTrack = () => {
		const nextSibling = audioRef.current.parentNode.nextSibling;
		if (nextSibling) {
			const nextAudio = nextSibling.querySelector("audio");
			if (nextAudio) {
				audioRef.current.pause();
				audioRef.current.currentTime = 0;
				nextAudio.play();
			}
		}
	};

	const prevTrack = () => {
		const prevSibling = audioRef.current.parentNode.previousSibling;
		if (prevSibling) {
			const prevAudio = prevSibling.querySelector("audio");
			if (prevAudio) {
				audioRef.current.pause();
				audioRef.current.currentTime = 0;
				prevAudio.play();
			}
		}
	};

	const toggleMute = () => {
		audioRef.current.muted = !audioRef.current.muted;
	};

	const changeVolume = (value) => {
		setVolume(value);
		audioRef.current.volume = value;
	};

	return (
		<div className="bg-gray-800 p-4 flex justify-between items-center">
			<audio
				ref={audioRef}
				controls></audio>

			<div className="flex items-center space-x-4">
				<button onClick={prevTrack}>
					<i className="fas fa-backward text-white"></i>
				</button>
				<button onClick={playPause}>
					<i
						className={`fas ${
							isPlaying ? "fa-pause" : "fa-play"
						} text-white`}></i>
				</button>
				<button onClick={nextTrack}>
					<i className="fas fa-forward text-white"></i>
				</button>
				<button onClick={toggleMute}>
					<i
						className={`fas ${
							audioRef.current && audioRef.current.muted
								? "fa-volume-mute"
								: "fa-volume-up"
						} text-white`}></i>
				</button>
				<input
					type="range"
					min="0"
					max="1"
					step="0.1"
					value={volume}
					onChange={(e) => changeVolume(e.target.value)}
					className="w-20 h-3 appearance-none rounded-full bg-gray-600 outline-none"
				/>
			</div>
		</div>
	);
}
