import React from 'react'

// import 'bootstrap/dist/css/bootstrap.min.css';
import { useState, useEffect } from 'react';
import './main.css'

export default function Search() {

  const CLIENT_ID = '3b6f5190c4d240628247ebaf818038cf'
  const CLIENT_SECRET = '3c489f19ce5d4162ba30695550340ae7'

  const [searchInput, setSearchInput] = useState('')
  const [accessToken, setAccessToken] = useState('')


  useEffect(() => {
    var authParameters = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded'
      },
      body: 'grant_type = client_credentials&client_id=' + CLIENT_ID + '&client_secret=' + CLIENT_SECRET
    }
    fetch('https://accounts.spotify.com/api/token' , authParameters)
    .then(result => result.json())
    .then(data => setAccessToken(data.access_token))
  }, [])

  async function search(){
    console.log("Search for" + searchInput)
  }
  return (
    <div className='search-pane'>
      <div className='arrows'>
        <i className='fas fa-arrow-left'></i>
        <i className='fas fa-arrow-right'></i>
      </div>
      <div className='search-container'>
        <i className='fas fa-search'></i>
        <form className='form-bar' onChange={event => setSearchInput(event.target.value)}>
        <input type='text' className='searchbar' placeholder='Search artist, albums and tracks...' />
        <button type='submit' className='btn' onClick={search}>Search</button>
        </form> 
      </div>


    </div>
  )
}
