import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './main.css';

export default function Search(props) {
  const [searchInput, setSearchInput] = useState('');
  const [artists, setArtists] = useState([]);
  const [tracks, setTracks] = useState([]);
  const token = window.localStorage.getItem('token');

  const search = async () => {
    const { data } = await axios.get('https://api.spotify.com/v1/search', {
      headers: {
        Authorization: `Bearer ${token}`,
      },
      params: {
        q: searchInput,
        type: 'artist,track', // Include both artist and track types in the search
      },
    });

    console.log(data);

    setArtists([...data.artists.items]);
    setTracks([...data.tracks.items]);
  };

  useEffect(() => {
    const delayDebounceFn = setTimeout(() => {
      if (searchInput) {
        search();
      }
    }, 300); // Delay for 300ms after user stops typing

    return () => clearTimeout(delayDebounceFn);
  }, [searchInput]);

  return (
    <div className="search-pane">
      <div className="arrows">
        <i className="fas fa-arrow-left"></i>
        <i className="fas fa-arrow-right"></i>
      </div>
      <div className="search-container">
        <i className="fas fa-search"></i>
        <form className="form-bar">
          <input
            onChange={(event) => setSearchInput(event.target.value)}
            type="text"
            className="searchbar"
            placeholder="Search artist, albums and tracks..."
          />
          {/* Remove the onClick event from the button */}
          <button type="submit" className="btn">
            Search
          </button>
        </form>
      </div>
    </div>
  );
}
